import './App.css';
import UserForm from './UseForm';
import UserView from './UserView';
export interface IUser {
  name: string;
  age: number;
}

export const UserContext = React.createContext<{
  user: IUser;
  dispatch: React.Dispatch<any>;
}>;

const App: React.FC = () => {
  const [user, setUser] = useState({ name: '', age: 0 });
  return (
    <div className='App'>
      <UserForm />
      <UserView />
    </div>
  );
};

export default App;
