import React, { useContext } from 'react';
import { UserContext } from './UserContext';
import { ActionType } from './actions/actions';

const UserForm = () => {
  const context = useContext(UserContext);

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    context?.dispatch({
      type: ActionType.ON_CHANGE,
      payload: { name, age },
    });
  };

  const handleClear = () => {
    context?.dispatch({ type: ActionType.CLEAR });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Name:
          <input type='text' value={context?.user.name} onChange={(e) => setName(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Age:
          <input type='number' value={context?.user.age} onChange={(e) => setAge(Number(e.target.value))} />
        </label>
      </div>
      <button type='submit'>Save</button>
      <button type='button' onClick={handleClear}>
        Clear
      </button>
    </form>
  );
};

export default UserForm;
